export const getValidDataArray = (data) => {
    const res = JSON.parse(data.result);
    return Array.isArray(res) ? res : []
};

export const getRandomInt = () => Math.floor(Math.random() * Math.floor(99999));

export const deleteUtil = (key, data) => {
    const arr = data.reduce((acc, elem) => {
        const accArray = (key === elem.key) ? acc : [...acc, elem];
        return accArray;
    }, []);
    return arr;
}

export const addOrUpdateUtil = (payload, data) => {
    const { _key, firstname, lastname, email } = payload;
    let found = false;
    let arr = data.reduce((acc, elem) => {
        found = found ? found : (_key === elem.key);
        const accArr = (_key === elem.key) ? [...acc, { key: _key, firstname, lastname, email }] : [...acc, elem];
        return accArr;
    }, []);
    arr = !found ? [...arr, { key: _key, firstname, lastname, email }] : arr;
    return arr;
}

export const getBaseUrl = url => `https://${url}/_tenant/demo/_fabric/addressbook/_fn/addressbook`;

export const getWsUrl = url => `wss://${url}/_ws/ws/v2/consumer/persistent/demo/c8local.addressbook/addresses/${getRandomInt()}`;

export const getProducerUrl = url => `wss://${url}/_ws/ws/v2/producer/persistent/demo/c8local.addressbook/addresses`;

export const makeRegionData = config => {
    const keys = Object.keys(config);
    const regionData = keys.reduce((acc, key) => {
        const obj = { label: key.toUpperCase(), value: config[key] };
        acc.push(obj);
        return acc;
    }, []);
    return regionData;
}
